Data data;

void Data::setup() {  
  verbose           = false; // use this for debugging purposes
  
  interpolating     = false;
  scene_count       = ARRAY_SIZE(PGMScenes);
  frame_count       = 0; 
  scene_pos         = -1;
  frame_pos         = -1;
  frames_in_between = 44 + 1; // empty frames enbetween two scenes, +1 because first frame of a scene is still part of the interpolation
  frames_at_start   = 70 + 1; // empty frames at the beginning of the film   
  
  steps[0] = steps[1] = steps[2] = 0; // _x _y _z
  frame[0] = frame[1] = frame[2] = frame[3] = frame[4] = 0; //_scene _frame _x _y _z

  tstart_pos[0] = tstart_pos[1] = tstart_pos[2] = 0; 
  tend_pos[0] = tend_pos[1] = tend_pos[2] = 0; 
  tlast_pos[0] = tlast_pos[1] = tlast_pos[2] = 0;
  tsteps[0] = tsteps[1] = tsteps[2] = 0;    
  
  if (verbose) {
    Serial.begin(115200);
  }
}

/* triggers the next scene 
 * at the beginning of a scene go first in transition mode by substracting 
 * frames_in_between or frames_at_start from the frame_pos
 */
int Data::nextScene() {
  if(scene_count - 1 > scene_pos) {
    scene_pos++;
    last_frame_pos = frame_pos;
    frame_pos = (scene_pos == 0) ? -frames_at_start-1 : -frames_in_between-1;
    frame_count = PGMFrameLengths[scene_pos];
    if (verbose) {
      Serial.print("Scene: "); Serial.println(scene_pos);
      Serial.print("Frame: "); Serial.println(frame_pos);
      Serial.print("Framecount: "); Serial.println(frame_count);
      Serial.print("Scenecount: "); Serial.println(scene_count);
    }
  }
  return scene_pos;
}

/* triggers the next frame and fills the position array 
 */
int Data::nextFrame() {
  if(frame_pos < frame_count - 1 ) {
    frame_pos++;
    if(frame_pos > 0) { // only if we'r not in the scene transition mode
      tsteps[0] = long(-1 * char(pgm_read_byte( PGMScenes[scene_pos] + (frame_pos * 3) + 0 )));
      tsteps[1] = long(-1 * char(pgm_read_byte( PGMScenes[scene_pos] + (frame_pos * 3) + 1 )));
      tsteps[2] = long(-1 * char(pgm_read_byte( PGMScenes[scene_pos] + (frame_pos * 3) + 2 )));
      
      interpolating = false;      
    } else if(scene_pos != 0 && scene_pos != -1) { // here we'r in transition mode
      /* get the last frame from last scene (lscene_lframe) and the first frame from this 
       * scene (tscene_fframe) for transition 
       */
			tstart_pos[0] = long(pgm_read_word( PGMStartEnd + ( scene_pos - 1 ) * 6 + 3 ));
			tstart_pos[1] = long(pgm_read_word( PGMStartEnd + ( scene_pos - 1 ) * 6 + 4 ));
			tstart_pos[2] = long(pgm_read_word( PGMStartEnd + ( scene_pos - 1 ) * 6 + 5 ));
			
			tend_pos[0] = long(pgm_read_word( PGMStartEnd + ( scene_pos ) * 6 + 0 ));
			tend_pos[1] = long(pgm_read_word( PGMStartEnd + ( scene_pos ) * 6 + 1 ));
			tend_pos[2] = long(pgm_read_word( PGMStartEnd + ( scene_pos ) * 6 + 2 ));

      /* interpolate between these two poss */
      interpolate(tstart_pos, tend_pos, tsteps, frames_in_between + frame_pos);
      interpolating = true;

    } else { // here we'r at the beginning of the film - send 70. times zero 
      tsteps[0] = 0; tsteps[1] = 0; tsteps[2] = 0; 
      interpolating = true;
    }
    /* fill the frame and steps array */
    steps[0] = tsteps[0]; 
    steps[1] = tsteps[1];	
    steps[2] = tsteps[2];
    
    frame[0] = scene_pos + 1;  
    frame[1] = frame_pos; 
    frame[2] += tsteps[0]; 
    frame[3] += tsteps[1]; 
    frame[4] += tsteps[2];     
    
  } else {
    steps[0] = steps[1] = steps[2] = 0;
  }
  return frame_pos;
}

/* returns count of scenes */
int Data::getSceneCount() {
  return scene_count;
}

/* returns count of frames of actual active scene */
int Data::getFrameCount() {
  return frame_count;
}

/* returns frameposition in actual scene */
int Data::getFramePos() {
  return frame_pos;
}

/* returns the camera position of the actual frame as Array() */
long* Data::getSteps() {
  return steps;
}

/* returns the actual frame (_scene_pos, _frame_pos, _x, _y, _z) 
 * simmilar to getSteps() except that it returns a absolute position;
 */
long* Data::getFrame() {
  return frame;
}

/* resets everything and goes to the first frame */
void Data::start() {
  reset();
  nextScene();
}

/* resets everything including scene_pos and frame_pos -> back to the start */
void Data::reset() {
  scene_pos   = -1;
  frame_pos   = -1;
  frame_count = 0;
  steps[0] = steps[1] = steps[2] = 0;
  frame[0] = frame[1] = frame[2] = frame[3] = frame[4] = 0;
}

/* returns if the maschine is interpolating right now */
bool Data::isInterpolating() {
  return interpolating;
}

int Data::availableMemory() 
{
  int size = 1024;
  byte *buf;
  while ((buf = (byte *) malloc(--size)) == NULL);
  free(buf);
  return size;
}

/* private stuff */
void Data::interpolate(long from[3], long to[3], long dst[3], int in_stack) 
{ 
  float mu = ( float(in_stack) / float(frames_in_between) );
  if (mu == 0.0) {
    tlast_pos[0] = from[0]; 
    tlast_pos[1] = from[1]; 
    tlast_pos[2] = from[2];
    if (verbose) {
      Serial.print("Interpolate from: ");
      Serial.print(word(from[0]));Serial.print(" ");
      Serial.print(word(from[1]));Serial.print(" ");
      Serial.print(word(from[2]));Serial.print(" ");
      Serial.print("to: ");
      Serial.print(word(to[0]));Serial.print(" ");
      Serial.print(word(to[1]));Serial.print(" ");
      Serial.println(word(to[2]));
    }
  }

  long tx = round(CosineInterpolate(float(from[0]), float(to[0]), mu));
  long ty = round(CosineInterpolate(float(from[1]), float(to[1]), mu));
  long tz = round(CosineInterpolate(float(from[2]), float(to[2]), mu));
  
  dst[0] = tx - tlast_pos[0]; 
  dst[1] = ty - tlast_pos[1]; 
  dst[2] = tz - tlast_pos[2];
  tlast_pos[0] = tx; 
  tlast_pos[1] = ty; 
  tlast_pos[2] = tz; 
}

float Data::CosineInterpolate(float y1, float y2, float mu) {
  float mu2;
  mu2 = (1.0-cos(mu*PI))/2.0;
  return(y1*(1.0-mu2)+y2*mu2);
}

